mapper.insert:
INSERT INTO T_FILE (IDX, BOARD_IDX, ORIGINAL_FILE_NAME, STORED_FILE_PATH, FILE_SIZE, CREATOR_ID, CREATE_DATETIME ) SELECT file_seq.nextval, A.* FROM (
SELECT #{item.boardIdx} as board_idx, #{item.originalFileName} as original_file_name, #{item.storedFilePath} as stored_file_path, #{item.fileSize} as file_size, 'admin', sysdate from DUAL
) A
